/**
 * UserProfile component for displaying user details.
 * 
 * Author: Adam Sarty
 * Student ID: B00794681
 * T4 - CSCI4177
 */

// Import necessary hooks and libraries
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useContentLoader } from '../js/useContentLoader.js';

function UserProfile() {
  useContentLoader(); // Custom hook for loading management

  const [user, setUser] = useState({}); // User state
  const { id } = useParams(); // Dynamic URL part
  const navigate = useNavigate(); // Navigation hook

  useEffect(() => { // Fetch user data on id change
    const fetchUser = async () => {
      const result = await axios(`https://express-t4.onrender.com/api/users/${id}`);
      setUser(result.data); // Set user data
    };
    fetchUser();
  }, [id]);

  const handleBack = () => navigate('/users'); // Navigate back

  // Render user profile
  return (
    <div className="container">
      <button onClick={handleBack}>Back</button>
      <img src={user.picture} alt={user.name} />
      <h1>{user.name}</h1>
      <h2>{user.email}</h2>
      <div className="detail-container">
        <div className="detail-box">
          <p><b>Company</b>: {user.company}</p>
          <p><b>Phone</b>: {user.phone}</p>
        </div>
        <div className="detail-box">
          <p>{user.about}</p>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
