import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useContentLoader } from '../js/useContentLoader.js';

function UserProfile() {
  useContentLoader();

  const [user, setUser] = useState({});
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const result = await axios(`https://express-t4.onrender.com/api/users/${id}`);
      setUser(result.data);
    };

    fetchUser();
  }, [id]);

  const handleBack = () => {
    navigate('/users');
  };

  return (
    <div className="container">
      <div className="content">
        <div className="profile">
          <button onClick={() => navigate('/users')}>Back</button>
          <img src={user.picture} alt={user.name} />
          <h1>{user.name}</h1>
          <h2>{user.email}</h2>
          <p>Company: {user.company}</p>
          <p>Phone: {user.phone}</p>
          <p>Address: {user.address}</p>
          <p>About: {user.about}</p>
          <p>Favorite Fruit: {user.favoriteFruit}</p>
          <div>
            <h3>Friends:</h3>
            <ul>
              {user.friends.map(friend => (
                <li key={friend.id}>{friend.name}</li>
              ))}
            </ul>
          </div>
          <div>
            <h3>Tags:</h3>
            {user.tags.map((tag, index) => (
              <span key={index} style={{ marginRight: '10px' }}>{tag}</span>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
