import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useContentLoader } from '../js/useContentLoader.js';

function UserProfile() {
  useContentLoader();

  const [user, setUser] = useState({});
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const result = await axios(`https://express-t4.onrender.com/api/users/${id}`);
      setUser(result.data);
    };

    fetchUser();
  }, [id]);

  const handleBack = () => {
    navigate('/users');
  };

  return (
    <div className="container">
      <div className="content">
        <div className="profile">
          <button onClick={handleBack}>Back</button>
          <img src={user.picture} alt={user.name} />
          <h1>{user.name}</h1>
          <h2>{user.email}</h2>
          <div className="detail">
            <span className="detail-label">Company:</span>
            <span className="detail-content">{user.company}</span>
          </div>
          <div className="detail">
            <span className="detail-label">Phone:</span>
            <span className="detail-content">{user.phone}</span>
          </div>
          <div className="detail">
            <span className="detail-label">Address:</span>
            <span className="detail-content">{user.address}</span>
          </div>
          <div className="about">
            <p>{user.about}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
